-- ----------------------------
--  Table structure for `qcc_basic_zhixing_items`
-- ----------------------------
DROP TABLE IF EXISTS `qcc_basic_zhixing_items`;
CREATE TABLE `qcc_basic_zhixing_items` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT 'id',
  `ent_name` varchar(500) NOT NULL DEFAULT '' COMMENT '公司名称',
  `credit_code` varchar(500) NOT NULL DEFAULT '' COMMENT '信用代码',
  `sourceid` varchar(500) NOT NULL DEFAULT '' COMMENT '官网系统ID',
  `liandate` varchar(500) NOT NULL DEFAULT '' COMMENT '立案时间',
  `anno` varchar(500) NOT NULL DEFAULT '' COMMENT '立案号',
  `execute_gov` varchar(500) NOT NULL DEFAULT '' COMMENT '执行法院',
  `biaodi` varchar(500) NOT NULL DEFAULT '' COMMENT '标地',
  `status` varchar(500) NOT NULL DEFAULT '' COMMENT '状态',
  `party_card_num` varchar(500) NOT NULL DEFAULT '' COMMENT '身份证号码/组织机构代码',
  `data_status` tinyint(4) NOT NULL DEFAULT '1' COMMENT '数据状态',
  `create_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `creator` varchar(500) DEFAULT NULL,
  `update_time` timestamp NULL DEFAULT NULL,
  `updater` varchar(500) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='企查查接口数据-企业关键字精确获取详细信息(Full)-执行';

-- ----------------------------
--  Table structure for `qcc_copyright`
-- ----------------------------
DROP TABLE IF EXISTS `qcc_copyright`;
CREATE TABLE `qcc_copyright` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `category` varchar(255) DEFAULT NULL COMMENT '分类号',
  `publish_date` datetime DEFAULT NULL COMMENT '发布日期',
  `version_no` int(11) DEFAULT NULL COMMENT '版本号',
  `register_no` varchar(255) DEFAULT NULL COMMENT '登记号',
  `register_aper_date` datetime DEFAULT NULL COMMENT '登记批准日期',
  `name` varchar(255) DEFAULT NULL COMMENT '软件全称',
  `short_name` varchar(255) DEFAULT NULL COMMENT '软件简称',
  `owner` varchar(255) DEFAULT NULL COMMENT '软件著作权人',
  `ent_name` varchar(255) DEFAULT NULL COMMENT '关键字(公司名或keyNo)',
  `create_time` timestamp NULL DEFAULT NULL COMMENT '创建时间',
  `update_time` timestamp NULL DEFAULT NULL COMMENT '更新时间',
  `remark` varchar(300) DEFAULT NULL COMMENT '备注',
  `version` int(10) DEFAULT NULL COMMENT '版本号',
  `data_status` int(1) DEFAULT NULL COMMENT '数据状态',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=1811 DEFAULT CHARSET=utf8 COMMENT='企查查API-软件著作权';

